'use strict';

var zeroFill = require('zero-fill');

function numberToHexCode(number, length, isZeroPadded) {
  var numberOfBits = 4 * length;
  var trimmedNumber = number % Math.pow(2, numberOfBits);
  var numberInHex = trimmedNumber.toString(16).slice(0, length);
  if (isZeroPadded) {
    return zeroFill(length, numberInHex);
  }
  return numberInHex;
}

module.exports = {
  numberToHexCode: numberToHexCode
};